from typing import Any, Dict, List
from langchain.embeddings.base import Embeddings
from langchain_community.vectorstores import Qdrant
from langchain_core.documents import Document
from qdrant_client.models import Filter
from langflow.custom import Component
from langflow.schema import Message
from langflow.io import (
    MessageInput,
    DataInput,
    Output,
)

from langflow.utils.qdrant import get_qdrant_client, check_collection_exists, create_collection, qdrant_inputs


class QdrantSearchFileComponent(Component):
    display_name = "Qdrant Search File"
    description = "Search in Qdrant Vector Store"
    icon = "Qdrant"

    inputs = [
        *qdrant_inputs,
        MessageInput(
            name="question",
            display_name="Question",
        ),
        DataInput(
            name="files",
            display_name="Files",
            list=True
        ),
    ]

    outputs = [
        Output(name="search",
               display_name="Search",
               method="search_documents"),
    ]

    def build_vector_store(self) -> Qdrant:
        try:
            collection_name: str = self.collection_name.get_text()
            embedding: Embeddings = self.embedding

            client = get_qdrant_client(
                self.qdrant_host.get_text(), self.qdrant_port.get_text())

            qdrant = Qdrant(client=client,
                            embeddings=embedding,
                            collection_name=collection_name)

            return qdrant

        except Exception as e:
            raise Exception(f"Error building vector store: {str(e)}")

    def search_documents(self) -> Message:

        question: str = self.question.get_text()

        if not question:
            return Message(text="No question provided")

        qdrant = self.build_vector_store()

        if not qdrant:
            return Message(text="No qdrant collection found")

        if not self.files:
            docs = qdrant.similarity_search(
                query=question,
                k=5
            )

            return Message(text="\n".join(f'{item.metadata.get('id')}:{item.metadata.get('title')}\n{item.page_content}\n\n' for item in docs))

        files: List[Dict[str, Any]] = [item.data for item in self.files]

        # Create a unique list of files based on id and version
        unique_files = []
        file_keys = set()

        for file in files:
            file_id = file.get('id', 'unknown')
            file_version = file.get('version', 1)
            file_key = f"{file_id}_{file_version}"

            if file_key not in file_keys:
                file_keys.add(file_key)
                unique_files.append(file)

        # Create OR conditions for each file (file_id AND version must match)
        file_conditions = [
            {
                "must": [
                    {"key": "metadata.id", "match": {
                        "value": file.get('id', 'unknown')}},
                    {"key": "metadata.version", "match": {
                        "value": file.get('version', 1)}}
                ]
            } for file in files
        ]

        filter: Filter = Filter(
            should=file_conditions
        )

        docs = qdrant.similarity_search(
            query=question,
            k=5,
            filter=filter
        )

        return Message(text="\n".join(f'{item.metadata.get('id')}:{item.metadata.get('title')}\n{item.page_content}\n\n' for item in docs))

from langflow.template.field.base import Output
from langflow.schema.data import Data
from langflow.schema.message import Message
from langflow.custom import Component
from langflow.inputs.inputs import DataInput, DictInput, IntInput
from langflow.schema.dotdict import dotdict
from langflow.field_typing.range_spec import RangeSpec
from typing import Any
import json


class DataMapper(Component):
    display_name = "Data Mapper"
    description = "Maps data using patterns or literal values to a new Data object or JSON string."
    icon = "table"
    name = "DataMapper"
    MAX_FIELDS = 15

    inputs = [
        DataInput(
            name="input_data",
            display_name="Data",
            info="Input data to map from.",
        ),
        IntInput(
            name="number_of_fields",
            display_name="Number of Fields",
            info="Number of field mappings to define.",
            real_time_refresh=True,
            value=0,
            range_spec=RangeSpec(min=0, max=MAX_FIELDS, step=1, step_type="int"),
        ),
    ]

    outputs = [
        Output(
            display_name="Data",
            name="data",
            method="build_data"
        ),
        Output(
            display_name="Message",
            name="message",
            method="build_message",
        ),
    ]

    def update_build_config(self, build_config: dotdict, field_value: Any, field_name: str | None = None):
        if field_name == "number_of_fields":
            default_keys = {
                "number_of_fields", 
                "input_data",
                "_type",
                "code"
            }

            try:
                field_value_int = int(field_value)
            except ValueError:
                return build_config

            if field_value_int > self.MAX_FIELDS:
                build_config["number_of_fields"]["value"] = self.MAX_FIELDS
                msg = f"Number of fields cannot exceed {self.MAX_FIELDS}."
                raise ValueError(msg)

            existing_fields = {}
            for key in list(build_config.keys()):
                if key not in default_keys:
                    existing_fields[key] = build_config.pop(key)

            for i in range(field_value_int):
                field_field = f"field_{i}"

                if field_field in existing_fields:
                    build_config[field_field] = existing_fields[field_field]
                else:
                    field = DictInput(
                        display_name=f"Field {i + 1}",
                        name=field_field,
                        info=f"Key-value pair where key is the output field name and value is either a pattern in curly brackets {{{{object.field}}}} or a literal value.",
                        input_types=["Message"],
                    )
                    build_config[field.name] = field.to_dict()

            build_config["number_of_fields"]["value"] = field_value_int
        return build_config

    def _get_value_from_path(self, data_dict, path):
        parts = path.split('.')
        current = data_dict

        for part in parts:
            if '[' in part and part.endswith(']'):
                field_name, index_part = part.split('[', 1)
                index = int(index_part[:-1])

                if field_name in current and isinstance(current[field_name], list):
                    if 0 <= index < len(current[field_name]):
                        current = current[field_name][index]
                    else:
                        return None
                else:
                    return None
            else:
                if isinstance(current, dict) and part in current:
                    current = current[part]
                else:
                    return None

        return current

    def _map_data(self):
        input_data = self.input_data
        fields = self._get_fields()

        result_data = {}

        if isinstance(input_data, Data):
            source_data = input_data.data
        elif isinstance(input_data, dict):
            source_data = input_data
        else:
            return {}

        for field_name, value in fields.items():
            if isinstance(value, str) and value.startswith('{') and value.endswith('}'):
                pattern = value[1:-1]
                extracted_value = self._get_value_from_path(source_data, pattern)

                if not field_name:
                    if '[' in pattern and pattern.split('.')[-1].endswith(']'):
                        last_part = pattern.split('.')[-1]
                        field_name = last_part.split('[')[0]
                    else:
                        field_name = pattern.split('.')[-1]

                if extracted_value is not None:
                    result_data[field_name] = extracted_value
            else:
                result_data[field_name] = value

        return result_data

    def _get_fields(self):
        fields = {}

        num_fields = getattr(self, "number_of_fields", 0)

        for i in range(num_fields): 
            field_field = f"field_{i}"

            if hasattr(self, field_field):
                field_input = getattr(self, field_field, {})

                if isinstance(field_input, dict) and field_input:
                    for key, value in field_input.items():
                        if isinstance(value, Message):
                            fields[key] = value.text
                        else:
                            fields[key] = value
                elif isinstance(field_input, Message):
                    fields[field_input.text] = field_input.text

        return fields

    def build_data(self) -> Data:
        result_data = self._map_data()
        return Data(data=result_data)

    def build_message(self) -> Message:
        result_data = self._map_data()
        try:
            json_string = json.dumps(result_data, indent=2, default=str)
            return Message(text=json_string)
        except Exception as e:
            return Message(text=f"Error converting to JSON: {str(e)}", error=True)

